QActions.QAction:
			using Skyline.DataMiner.Scripting;
using System;

public class QAction
{
    public static void Run(SLProtocolExt protocol)
    {
        double dblOutageWeight = (double)protocol.GetParameterIndexByKey(protocol.array_alarm_store.TableId, protocol.RowKey(), Parameter.Array_alarm_store.Idx.column_impact + 1);
        double dblOutageCorrected = (double)protocol.GetParameterIndexByKey(protocol.array_alarm_store.TableId, protocol.RowKey(), 11);
        double dblViolation = (double)protocol.GetParameterIndexByKey(protocol.array_alarm_store.TableId, protocol.RowKey(), 16);
        double dblInput = (double)protocol.GetParameter(53);

        //Calculate Valiolation
        dblViolation = dblOutageCorrected * dblOutageWeight / 100;
        //Check for negative corrections
        if (dblInput < 0) dblInput = dblViolation;
        //Correction larger than violation
        else if (dblInput > dblViolation) dblInput = dblViolation;
        dblViolation -= dblInput;

        protocol.SetParameterIndexByKey(protocol.array_alarm_store.TableId, protocol.RowKey(), Parameter.Array_alarm_store.Idx.column_correction + 1, dblInput);
        protocol.SetParameterIndexByKey(protocol.array_alarm_store.TableId, protocol.RowKey(), Parameter.Array_alarm_store.Idx.column_violation + 1, dblViolation); 
    }
}


using System;
using System.Collections.Generic;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Manual Outage Delete
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer</param>
	public static void Run(SLProtocolExt protocol)
	{
		var allOutagesToDelete = new List<string>();

		var sKeysToDelete = Convert.ToString(protocol.Manual_outage_delete_pk_143).Split(new[] { ';' }, StringSplitOptions.RemoveEmptyEntries);

		if (sKeysToDelete.Length > 20)
		{
			var oOutageTable = (object[])protocol.NotifyProtocol(
				321,
				Parameter.Array_alarm_store.tablePid /*37*/,
				new UInt32[]
				{
					Parameter.Array_alarm_store.Idx.column_index_47,
					Parameter.Array_alarm_store.Idx.column_severity_38
				});
			var oOutagePks = (object[])oOutageTable[0];
			var oOutageType = (object[])oOutageTable[1];
			var keyToType = new Dictionary<string, object>();

			for (var i = 0; i < oOutagePks.Length; i++)
			{
				keyToType[Convert.ToString(oOutagePks[i])] = oOutageType[i];
			}

			foreach (var sKeyToDelete in sKeysToDelete)
			{
				if (!keyToType.ContainsKey(sKeyToDelete))
				{
					continue;
				}
				// Check type, only delete manual outages
				var iOutageType = Convert.ToInt32(keyToType[sKeyToDelete]);
				if (iOutageType == 12)
				{
					allOutagesToDelete.Add(sKeyToDelete);
				}

				// Make sure we don't delete the same key twice by failing the next exist lookup
				keyToType.Remove(sKeyToDelete);
			}
		}
		else
		{
			foreach (var sKeyToDelete in sKeysToDelete)
			{
				if (!protocol.array_alarm_store.Exists(sKeyToDelete))
				{
					continue;
				}

				var iOutageType = Convert.ToInt32(protocol.array_alarm_store[sKeyToDelete, Parameter.Array_alarm_store.Idx.column_severity_38]);
				if (iOutageType == 12)
				{
					// Only delete manual outages
					allOutagesToDelete.Add(sKeyToDelete);
				}
			}
		}

		if (allOutagesToDelete.Count != 0)
		{
			protocol.array_alarm_store.DeleteRow(allOutagesToDelete);
		}
	}
}


using System;
using System.IO;
using Skyline.DataMiner.Scripting;
using System.Text.RegularExpressions;
using System.Globalization;

public class QAction
{
    public static void Run(SLProtocolExt protocol)
    {
        String input = Convert.ToString(protocol.GetParameter(protocol.GetTriggerParameter()));

        try
        {			
            DateTime dt = DateTime.ParseExact(input, "yyyy/MM/dd HH:mm:ss", CultureInfo.InvariantCulture);

            switch (protocol.GetTriggerParameter())
            {
                case 69:
                    protocol.SetParameter(Parameter.sla_validity_start_time, input);
                    break;
                case 71:
                    protocol.SetParameter(Parameter.sla_validity_end_time, input);
                    break;
            }
        }
        catch (Exception)
        {
			protocol.Log(string.Format("QA{0} Parsing validity time {1} failed: ", protocol.QActionID, input), LogType.Error, LogLevel.NoLogging);
        }
    }
}



using System;
using System.Collections.Generic;
using Skyline.DataMiner.Scripting;

public class QAction
{
	public static void Run(SLProtocolExt protocol, object extraData)
	{
		var sa = extraData as string[];
		if (sa == null || sa.Length < 2)
		{
			return;
		}

		try
		{
			// sa[0] = unique client id
			// sa[1] = command (= value of discreet defined on this parameter)
			// sa[n] = optional arguments depending on command
			switch (sa[1])
			{
				case "deleteoutages":
					DeleteOutages(protocol, sa, 2);
					break;
				case "default":
					return;
			}
		}
		catch (Exception e)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error during context menu row deletion:" + e, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void DeleteOutages(SLProtocolExt protocol, string[] keys, int startIndex)
	{
		var allOutagesToDelete = new List<string>();
		var ignoredOutages = new List<string>();

		if (keys.Length - startIndex > 20)
		{
			var oOutageTable = (object[])protocol.NotifyProtocol(
				321,
				Parameter.Array_alarm_store.tablePid /*37*/,
				new UInt32[]
				{
					Parameter.Array_alarm_store.Idx.column_index_47,
					Parameter.Array_alarm_store.Idx.column_severity_38
				});
			var oOutagePks = (object[])oOutageTable[0];
			var oOutageType = (object[])oOutageTable[1];
			var keyToType = new Dictionary<string, object>();

			for (var i = 0; i < oOutagePks.Length; i++)
			{
				keyToType[Convert.ToString(oOutagePks[i])] = oOutageType[i];
			}

			for (var i = startIndex; i < keys.Length; i++)
			{
				if (!keyToType.ContainsKey(keys[i]))
				{
					continue;
				}

				// Check type, only delete manual outages
				var iOutageType = Convert.ToInt32(keyToType[keys[i]]);
				if (iOutageType == 12)
				{
					allOutagesToDelete.Add(keys[i]);
				}
				else
				{
					ignoredOutages.Add(keys[i]);
				}

				// Make sure we don't delete the same key twice by failing the next exist lookup
				keyToType.Remove(keys[i]);

			}
		}
		else
		{
			for (var i = startIndex; i < keys.Length; i++)
			{
				if (!protocol.array_alarm_store.Exists(keys[i]))
				{
					continue;
				}

				var iOutageType = Convert.ToInt32(protocol.array_alarm_store[keys[i], Parameter.Array_alarm_store.Idx.column_severity_38]);
				if (iOutageType == 12)
				{
					// Only delete manual outages
					allOutagesToDelete.Add(keys[i]);
				}
				else
				{
					ignoredOutages.Add(keys[i]);
				}
			}
		}

		if (allOutagesToDelete.Count != 0)
		{
			protocol.array_alarm_store.DeleteRow(allOutagesToDelete);
		}

		if (ignoredOutages.Count > 0)
		{
			protocol.SetParameter(
				Parameter.array_alarm_store_qactionfeedback_79,
				keys[0]
				+ "|INFO|The following selected outages were not deleted because they were not created manually:\r\n"
				+ string.Join(", ", ignoredOutages));
		}
	}
}


using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Text;
using Skyline.DataMiner.Scripting;

public class QAction
{
	private const string DtFormat = "yyyy/MM/dd HH:mm:ss";

	/// <summary>
	/// Validate User Input
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner</param>
	public static void Run(SLProtocolExt protocol)
	{
		var trigger = protocol.GetTriggerParameter();
		if (trigger == Parameter.Write.manual_outage_start_time_client_input_148)
		{
			var startDt = ParamToDt(protocol.Manual_outage_start_time_client_input_148);
			var endDt = ParamToDt(protocol.Manual_outage_end_time_client_input_149);

			protocol.Manual_outage_start_time_client_input_147 = startDt.ToOADate();
			protocol.Manual_outage_start_time_134 = startDt.ToString(DtFormat);

			if (startDt > endDt)
			{
				// End date-time is incorrect, so we set them to the start date-time
				protocol.Manual_outage_end_time_client_input_149 = startDt.ToOADate();
				protocol.Manual_outage_end_time_136 = startDt.ToString(DtFormat);
			}
		}
		else
		{
			var startDt = ParamToDt(protocol.Manual_outage_start_time_client_input_147);
			var endDt = ParamToDt(protocol.Manual_outage_end_time_client_input_150);

			if (startDt > endDt)
			{
				// End date-time is incorrect, so we set them to the start date-time
				protocol.Manual_outage_end_time_client_input_149 = startDt.ToOADate();
				protocol.Manual_outage_end_time_136 = startDt.ToString(DtFormat);
			}
			else
			{
				protocol.Manual_outage_end_time_client_input_149 = endDt.ToOADate();
				protocol.Manual_outage_end_time_136 = endDt.ToString(DtFormat);
			}
		}
	}

	private static DateTime ParamToDt(object paramValue)
	{
		if (!(paramValue is double))
		{
			return new DateTime();
		}
		return DateTime.FromOADate((double)paramValue);
	}
}


using System;
using System.IO;
using Skyline.DataMiner.Scripting;
using System.Text.RegularExpressions;

public class QAction
{
	public static void Run(SLProtocolExt protocol)
	{
		//Set the create ticket parameter to create ticket! (the automations script will reset the parameter).
		protocol.Generateticket_600 = 1;

		string strNewOutageIndex = "";
		string strDetails = "";

		//Retrieve the index of the newly added outage row.
		strNewOutageIndex = Convert.ToString(protocol.Trigger_dummy_row_added_350);
		//protocol.Log(8, 5, "strNewOutageIndex = " + strNewOutageIndex);

		try
		{
			int iHealthState = 2;
			//check if this key exists in the OutageTable
			if (protocol.array_alarm_store.Exists(strNewOutageIndex))
			{
				//get the details
				object[] outageRow = (object[])protocol.array_alarm_store.GetRow(strNewOutageIndex);
				strDetails = Convert.ToString(outageRow[2]) + ";" + Convert.ToString(outageRow[6]) + ";" + strNewOutageIndex;							
				iHealthState = 1;
			}

			//Set the outage details in the parameter used as input for the ticket request automation script.								
			protocol.Outagedetails_500 = strDetails;

			//Set SLA health status to good if it was different in the past.
			if (Convert.ToDouble(protocol.Sla_health_status_70) != iHealthState)
				protocol.Sla_health_status_70 = iHealthState;

		}
		catch (Exception ex1)
		{
			protocol.Log(string.Format("QA{0} Retrieving Details ({1}) for Ticket Creation Failed: {2}", protocol.QActionID, strDetails, ex1.ToString()),LogType.Error,LogLevel.NoLogging);

			//Set SLA health status to bad since the retrieval of the outage information failed.
			protocol.Sla_health_status_70 = 2;
		}
	}
}


