using System;
using System.IO;
using System.Text;
using System.Linq;
using System.Collections;
using System.Collections.Generic;
using System.Globalization;
using Skyline.DataMiner.Scripting;

public class QAction
{
    /// <summary>
    /// Update History Statistics
    /// </summary>
    /// <param name="protocol">Link with Skyline Dataminer</param>
    public static void Run(SLProtocolExt protocol)
    {
        int iTrigger = protocol.GetTriggerParameter();

        String startWindow = Convert.ToString(protocol.GetParameter(31));
        String endWindow = Convert.ToString(protocol.GetParameter(32));

        if(startWindow =="" || endWindow =="") return;
		DateTime window_start, window_end;
		if (!DateTime.TryParse(startWindow, out window_start)) return;
		if(!DateTime.TryParse(endWindow, out window_end))return;

        int window_unit = Convert.ToInt32(protocol.GetParameter(103));

        string currentidx = Convert.ToString(protocol.GetParameter(1051));
        string newidx = GetIdx(window_unit, window_start);

        if (currentidx != newidx)
        {
            //update copy the stored end time
            object storedendtime = protocol.GetParameterIndexByKey(1000, currentidx, 4);
            protocol.SetParameterIndexByKey(1000, currentidx, 3, storedendtime);

            //new idx
            protocol.SetParameter(1051, newidx);
            currentidx = newidx;

        }

        if (String.IsNullOrWhiteSpace(currentidx)) return;

        if (iTrigger == 31) // window start time changed
        {
            if (!protocol.Exists(1000, currentidx))
            {
                HistoryStatisticsTableQActionRow newrow = new HistoryStatisticsTableQActionRow()
                {
                    TrackingPeriod = currentidx,
                    StartTimeHistory = window_start.ToString("yyyy-MM-dd HH:mm:ss"),
                    EndTimeHistory = "-1",
                    StoredEndTimeHistory = window_end.ToString("yyyy-MM-dd HH:mm:ss"),
                    ComplianceHistory = 0,
                    AvailabilityHistory = 100,
                    AvailabilityWithoutCorrectionsHistory = 100,
                    TotalViolationTimeHistory = 0,
                    LongestViolationTimeHistory = 0,
                    NumberOfViolationsHistory = 0
                };

                protocol.historyStatisticsTable.AddRow(newrow);

                //limit number of rows
                LimitRows(protocol);
            }
        }
        else // value changed
        {
            if (!protocol.Exists(1000, currentidx))
                return;

            object value = protocol.GetParameter(iTrigger);

            switch (iTrigger)
            {
                case 10: // compliance
                    protocol.SetParameterIndexByKey(1000, currentidx, 5, value);
                    break;
                case 48: // availability
                    protocol.SetParameterIndexByKey(1000, currentidx, 6, value);
                    break;
                case 65: // availability without corrections
                    protocol.SetParameterIndexByKey(1000, currentidx, 7, value);
                    break;
                case 21: // total violation time
                    protocol.SetParameterIndexByKey(1000, currentidx, 8, value);
                    break;
                case 22: // longest violation time
                    protocol.SetParameterIndexByKey(1000, currentidx, 9, value);
                    break;
                case 24: // number of violations
                    protocol.SetParameterIndexByKey(1000, currentidx, 10, value);
                    break;
            }
        }

    }

    private static void LimitRows(SLProtocolExt protocol)
    {
        int table = 1000;

        if (protocol.IsEmpty(1052))
            protocol.SetParameter(1052, 1000);

        int maxrows = Convert.ToInt32(protocol.GetParameter(1052));

        int rowcount = protocol.RowCount(table);
        if (rowcount > maxrows)
        {
            try
            {
                string[] delkeys = protocol.GetKeys(table).OrderBy(k => Convert.ToDateTime(protocol.GetParameterIndexByKey(table, k, 2))).Take(rowcount - maxrows).ToArray();
                protocol.DeleteRow(table, delkeys);
            }
            catch
            {
                // A DateTime Conversion has failed
                // Find and Delete entries with unConvertable DateTimeStrings
                // While going over all entries and converting them, set all Converted strings back with the correct format
                List<string> liDelKeys = new List<string>();
                foreach (string key in protocol.GetKeys(table))
                {
                    DateTime dtTime;
                    bool bSucces = false;
                    string strToConvert = protocol.GetParameterIndexByKey(table, key, 2).ToString();
                    try
                    {
                        bSucces = DateTime.TryParse(strToConvert, out dtTime);
                        string strCorrectFormat = dtTime.ToString("yyyy-MM-dd HH:mm:ss");
                        if (bSucces)
                        {
                            if (string.Compare(strToConvert, strCorrectFormat) != 0)
                            {
                                //When Conversion succeeds and changes (in format) has occurred, put the datetime with correct format in place 
                                protocol.SetParameterIndexByKey(table, key, 2, strCorrectFormat);
                                protocol.Log(8, -1, string.Format("Converted {0} to {1}", strToConvert, strCorrectFormat));
                            }
                        }
                        else
                        {
                            //When Conversion fails, remove entry
                            liDelKeys.Add(key);
                            protocol.Log(8, -1, string.Format("Delete key: {0}; Could not convert {1} to {2}", key, strToConvert, strCorrectFormat));
                        }
                    }
                    catch(Exception e)
                    {
                        //When conversion fails bad, catch exception and delete bad entry
                        liDelKeys.Add(key);
                        protocol.Log(8, -1, string.Format("Delete key: {0}; Could not convert {1} because of Exception: {2}", key, strToConvert, e.Message));
                    }
                }
                //Delete rows with troublesome DateTimes
                protocol.DeleteRow(table, liDelKeys.ToArray());
            }
        }
    }

    private static string GetIdx(int window_unit, DateTime window_start)
    {
        string idx = "";

        switch (window_unit)
        {
            case 0: // Undefined

                break;
            case 1: // Days
                idx = window_start.ToString("yyyy-MM-dd");
                break;
            case 2: // Weeks
                idx = window_start.ToString("yyyy") + " week " + GetWeekNumber(window_start);
                break;
            case 3: // Months
                idx = window_start.ToString("yyyy-MM");
                break;
            case 4: // Hours
                idx = window_start.ToString("yyyy-MM-dd HH");
                break;
            case 5: // Minutes
                idx = window_start.ToString("yyyy-MM-dd HH:mm");
                break;
        }

        return idx;
    }

    private static int GetWeekNumber(DateTime dtPassed)
    {
        CultureInfo ciCurr = CultureInfo.CurrentCulture;
        int weekNum = ciCurr.Calendar.GetWeekOfYear(dtPassed, CalendarWeekRule.FirstFourDayWeek, DayOfWeek.Monday);
        return weekNum;
    }
}


