				using System;
				using System.IO;
				using System.Text;
				using System.Linq;
				using System.Collections;
				using System.Collections.Generic;
				using System.Globalization;
				using Skyline.DataMiner.Scripting;

				public class QAction
				{
					/// <summary>
					/// Clear History Statistics Table
					/// </summary>
					/// <param name="protocol">Link with Skyline Dataminer</param>
					public static void Run(SLProtocol protocol)
					{

                         int iTrigger = protocol.GetTriggerParameter();
                         String sKey= protocol.RowKey();

                         UInt32 newValue=Convert.ToUInt32(protocol.GetParameter(iTrigger));

                         protocol.Log(8,5,String.Format("{0}/{1}/{2}",iTrigger,sKey,newValue));

                         UInt32 window_start = Convert.ToUInt32(protocol.GetParameterIndexByKey(400,sKey,3));
                         UInt32 window_end = Convert.ToUInt32(protocol.GetParameterIndexByKey(400,sKey,4));

						if(iTrigger == 413)
						{
							//StartTime
							if(newValue < window_end && newValue >= 0 && newValue < 1439)
								protocol.SetParameterIndexByKey(400,sKey,3,newValue);
							else
								protocol.Log(8,5,String.Format("Window Start value not correct. (Window Start Value must be: Lesser than Window End Value, Greater than or equal to 0, lesser than 23:59)"));
						}
						else
						{
							//EndTime
							if(newValue > window_start && newValue > 0 && newValue <= 1439)
								protocol.SetParameterIndexByKey(400,sKey,4,newValue);
							else
								protocol.Log(8,5,String.Format("Window End value not correct. (Window End Value must be: Greater than Window Start Value, Greater than 0, lesser than or equal to 23:59)"));
						}
					}
				}


using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Text;
using System.Linq;
using System.Xml.Serialization;
using Skyline.DataMiner.Scripting;
using Skyline.DataMiner.Net.Messages;
using Skyline.Protocol;
public class QAction
{
	/// <summary>
	/// Save/LoadConfig
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer</param>
	public static void Run(SLProtocol protocol)
	{
		/*---------------------------------
		* To Add or Remove Parameters from the configuration save, simply adjust the static Mapper Class
		* ---------------------------------
		* SLA-ConfigurationFiles are in readable XML format and are processed using XmlSerializer that maps Object Model to XML, for ease of use:
		* <SLA>
		*     <Parameters>
		*      <Param>
		*          <PID/>
		*          <Name/>
		*          <Value/>
		*      </Param>
		*      </Parameters>
		*      <Tables>
		*         <Table>
		*          <PID/>
		*          <Name/>
		*           <Rows>
		*            <Row>
		*                <Key/>
		*               <Columns>
		*                <Column>
		*                    <IDX/>
		*                    <Name/>
		*                    <Value/>
		*                </Column>
		*               </Columns>
		*            </Row>
		*           </Rows>
		*          </Table>
		*      </Tables>
		* </SLA>
		* <Compliance/>
		* <Violation/>
		* -----------------------------------------------------
		* */

		var error = new Error();
		var handler = new Handler(protocol, error);

		try
		{
			handler.Handle();
		}
		catch (Exception e)
		{
			handler.error.ErrorOccured = true;
			handler.error.SetMessage(Error.AppendPolicy.None, "Failed (Exception on Top-Level Run): ", e);
		}
		finally
		{
			protocol.SetParameter(1064, handler.error.GetMessage());
		}
	}
}

namespace Skyline.Protocol
{
	using Model;

	public class Handler
	{
		private readonly SLProtocol protocol;
		public Error error;

		public Handler(SLProtocol protocol, Error error)
		{
			this.protocol = protocol;
			this.error = error;
		}

		public void Handle()
		{
			const string saveLocation = @"C:\Skyline DataMiner\Documents\SLA Configurations\";

			//Check if Dir Exists
			if (!Directory.Exists(saveLocation))
			{
				protocol.CreateFolder("SLA Configurations");
			}

			var load = Convert.ToInt32(protocol.GetParameter(protocol.GetTriggerParameter())); // 0 or 1 returned
			var name = Convert.ToString(protocol.GetParameter(1060));
			if (string.IsNullOrEmpty(name))
			{
				error.ErrorOccured = true;
				error.SetMessage("Failed: Name was Null or Empty");
				return;
			}

			if (name[0] == '\\' || name[0] == '/')
			{
				name = name.Substring(1);
			}

			var fullPath = saveLocation + name;
			if (!fullPath.EndsWith(".xml"))
			{
				fullPath += ".xml";
			}

			switch (load)
			{
				case 0:
					protocol.SetParameter(1064, "Saving...");
					var saver = new Saver(protocol, fullPath, error);
					saver.Save();
					protocol.SetParameter(1065, 1); //Reload Configurations
					break;
				case 1:
					protocol.SetParameter(1064, "Loading...");
					var loader = new Loader(protocol, fullPath, error);
					loader.Load();
					break;
				case 2:
					protocol.SetParameter(1064, "Deleting...");
					try
					{
						if (File.Exists(fullPath))
						{
							File.Delete(fullPath);

							protocol.FileChanged(fullPath, ProtocolExtensions.FileChangeType.Removed);

							error.SetMessage(Error.AppendPolicy.None, "Successfully deleted configuration: ", fullPath);
							protocol.SetParameter(1065, 1); //Reload Configurations
						}
						else
						{
							error.ErrorOccured = true;
							error.SetMessage(Error.AppendPolicy.None, "Failed to Delete Configuration file: ", fullPath);
						}
					}
					catch (Exception e)
					{
						error.ErrorOccured = true;
						error.SetMessage(Error.AppendPolicy.None, "Failed to Delete Configuration file: ", fullPath);
						protocol.Log(8, 5, "ERR: Failed to Delete Configuration file: " + fullPath);
						protocol.Log(8, 5, "Exception:" + e);

						//CONTINUE HERE
					}
					break;
			}
		}
	}

	public class Saver
	{
		SLProtocol protocol;
		string fullPath;
		private Error error;

		public Saver(SLProtocol protocol, string fullPath, Error error)
		{
			this.protocol = protocol;
			this.fullPath = fullPath;
			this.error = error;
		}

		public void Save()
		{
			//Creating the XML
			var config = new Configuration();
			CreatePageXML("SLA", config.SLA.Parameters, config.SLA.Tables, Mapper.SLAParamMapping, null);
			CreatePageXML("Compliance", config.Compliance.Parameters, config.Compliance.Tables, Mapper.ComplianceParamMapping, null);
			CreatePageXML("Violation", config.Violation.Parameters, config.Violation.Tables, null, Mapper.ViolationTableMapping);

			var changeType = File.Exists(fullPath)
				? ProtocolExtensions.FileChangeType.Changed
				: ProtocolExtensions.FileChangeType.Added;

			try
			{
				var xmlSerializer = new XmlSerializer(typeof(Configuration));
				try
				{
					using (TextWriter xmlWriter = new StreamWriter(fullPath))
					{
						xmlSerializer.Serialize(xmlWriter, config);
					}
				}
				catch (Exception e)
				{
					error.ErrorOccured = true;
					error.SetMessage(Error.AppendPolicy.None, "Failed (Exception at Serializing -saving- to File): ", e);
					return;
				}
			}
			catch (Exception e)
			{
				error.ErrorOccured = true;
				error.SetMessage(Error.AppendPolicy.None, "Failed (Exception at Creating XML using Serializer): ", e);
				return;
			}

			//Tell the dataminer system that a file has been added or updated.
			protocol.FileChanged(fullPath, changeType);

			if (error.ErrorOccured)
			{
				error.PrefixMessage(true, "Save Partially Successful ! Errors occurred, see Below:");
			}
			else
			{
				error.SetMessage("Save Successful!");
			}
		}

		public void CreatePageXML(
			string page,
			List<Param> parameters,
			List<Table> tables,
			Dictionary<uint, string> paramMapping,
			Dictionary<string, string> tableMapping)
		{
			#region parameters

			try
			{
				MapParameters(page, parameters, paramMapping);
			}
			catch (Exception e)
			{
				error.ErrorOccured = true;
				error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed Creating ", page, " (Exception While Creating SLA):");
				error.AddMessage(e);
			}

			#endregion

			#region tables

			try
			{
				MapTables(page, tables, tableMapping);
			}
			catch (Exception e)
			{
				error.ErrorOccured = true;
				error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed on Page", page, " processing ViolationMapping (Exception While Processing ViolationMapping):");
				error.AddMessage(e);
			}

			#endregion
		}

		private void MapParameters(string page, List<Param> parameters, Dictionary<uint, string> paramMapping)
		{
			if (paramMapping == null || paramMapping.Count <= 0)
			{
				return;
			}

			var pids = paramMapping.Keys.ToArray();
			var results = (object[])protocol.GetParameters(pids);

			if (results != null)
			{
				for (var i = 0; i < results.Length; i++)
				{
					var pid = pids[i];
					try
					{
						if (results[i] != null)
						{
							object result = Mapper.NULLSTRING;
							if (!protocol.IsEmpty(Convert.ToInt32(pid)))
							{
								result = results[i];
							}
							parameters.Add(new Param(pid, paramMapping[pid], result));
						}
						else
						{
							error.ErrorOccured = true;
							error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed Creating ", page, " PID:", pid, " Name: ", paramMapping[pid]);
						}
					}
					catch
					{
						error.ErrorOccured = true;
						error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed Creating ", page, " PID:", pid, " Name: ", paramMapping[pid]);
					}
				}
			}
			else
			{
				error.ErrorOccured = true;
				error.AddMessage(Error.AppendPolicy.None, "Failed Creating ", page, " (GetParameters Returned Empty)");
			}
		}

		private void MapTables(string page, List<Table> tables, Dictionary<string, string> tableMapping)
		{
			if (tableMapping == null || tableMapping.Count <= 0)
			{
				return;
			}

			var tableIDs = new Dictionary<int, List<uint>>();
			var tableMaxIdxs = new Dictionary<int, uint>();

			//Collecting all Data from TableMapping and converting into usable format.
			foreach (var v in tableMapping)
			{
				var key = v.Key;
				var keyA = key.Split('/');
				if (keyA.Length > 1)
				{
					var dictionaryKey = Convert.ToInt32(keyA[0]);
					var dictionaryValue = Convert.ToUInt32(keyA[1]);

					if (!tableIDs.ContainsKey(dictionaryKey))
					{
						tableIDs[dictionaryKey] = new List<uint>();
					}
					if (!tableMaxIdxs.ContainsKey(dictionaryKey))
					{
						tableMaxIdxs[dictionaryKey] = dictionaryValue;
					}
					tableIDs[dictionaryKey].Add(dictionaryValue);
					if (tableMaxIdxs[dictionaryKey] < dictionaryValue)
						tableMaxIdxs[dictionaryKey] = dictionaryValue; //Finding highest IDX (necessary for setrows in the Loader)
				}
				else
				{
					tableIDs[Convert.ToInt32(key)] = new List<uint>();
				}
			}

			//Iterating over all requested Tables
			foreach (var table in tableIDs)
			{
				var tableID = table.Key;
				try
				{
					var columnsToRetrieve = table.Value.ToArray();
					var columns = (object[])protocol.NotifyProtocol(321 /*NT_GT_TABLE_COLUMNS*/, tableID, columnsToRetrieve);
					var keys = (object[])columns[0];
					var rowsDic = new Dictionary<string, Row>();

					if (keys != null)
					{
						for (var c = 0; c < columns.Length; c++)
						{
							var idx = columnsToRetrieve[c];
							var mapKey = tableID + "/" + idx;
							var name = tableMapping[mapKey];

							var column = (object[])columns[c];
							if (column != null)
							{
								for (var r = 0; r < column.Length; r++)
								{
									var rowKey = Convert.ToString(keys[r]);
									if (!rowsDic.ContainsKey(rowKey))
									{
										var highestIDX = tableMaxIdxs[tableID];
										rowsDic.Add(rowKey, new Row(rowKey, highestIDX));
									}
									var value = column[r];
									var innerColValue = new Column(idx, name, value);
									rowsDic[rowKey].Columns.Add(innerColValue);
								}
							}
							else
							{
								error.ErrorOccured = true;
								error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed on Page ", page, " to retrieve Column:", idx, "-", name, " From table:", tableID);
							}
						}

						tables.Add(new Table(tableID, tableMapping[Convert.ToString(tableID)], rowsDic.Values.ToList()));
					}
				}
				catch (Exception e)
				{
					error.ErrorOccured = true;
					error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed on Page ", page, " Creating Table", tableID, " (Exception While Creating ViolationTable):");
					error.AddMessage(e);
				}
			}
		}
	}

	public class Loader
	{
		SLProtocol protocol;
		string fullPath;
		private Error error;

		public Loader(SLProtocol protocol, string fullPath, Error error)
		{
			this.protocol = protocol;
			this.fullPath = fullPath;
			this.error = error;
		}

		public void Load()
		{
			if (File.Exists(fullPath))
			{
				var xmlSerializer = new XmlSerializer(typeof(Configuration));
				try
				{
					using (TextReader tr = new StreamReader(fullPath))
					{
						var config = (Configuration)xmlSerializer.Deserialize(tr);
						LoadPage("SLA", config.SLA.Parameters, config.SLA.Tables);
						LoadPage("Compliance", config.Compliance.Parameters, config.Compliance.Tables);
						LoadPage("Violations", config.Violation.Parameters, config.Violation.Tables);
					}
				}
				catch (Exception e)
				{
					error.ErrorOccured = true;
					error.SetMessage(Error.AppendPolicy.AfterLastPart, "Failed (Exception at Serializing -Loading- from File): ");
					error.AddMessage(e);
					return;
				}
			}
			else
			{
				error.SetMessage("Failed Loading: File Does Not Exist!");
				return;
			}

			if (error.ErrorOccured)
			{
				error.PrefixMessage(true, "Load Partially Successful ! Errors occurred, see Below:");
			}
			else
			{
				error.SetMessage("Load Successful!");
			}
		}

		private void LoadPage(string page, List<Param> parameters, List<Table> tables)
		{
			try
			{
				LoadParameters(parameters);
				LoadTables(tables);
			}
			catch (Exception e)
			{
				error.ErrorOccured = true;
				error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed (Exception Occurred Loading ", page, "):");
				error.AddMessage(e);
			}
		}

		private void LoadParameters(List<Param> parameters)
		{
			if (parameters == null)
			{
				return;
			}

			foreach (var p in parameters)
			{
				try
				{
					if (Convert.ToString(p.Value) != Mapper.NULLSTRING)
					{
						protocol.SetParameter(Convert.ToInt32(p.PID), p.Value);
					}
					else
					{
						//Clear this Parameter
						var clearTrigger = Convert.ToInt32(p.PID) + 1000;
						protocol.CheckTrigger(clearTrigger);
					}
				}
				catch
				{
					error.ErrorOccured = true;
					error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed to Load parameter with PID:", p.PID);
				}
			}
		}

		private void LoadTables(List<Table> tables)
		{
			if (tables == null)
			{
				return;
			}

			foreach (var t in tables)
			{
				//Clear Table
				protocol.ClearAllKeys(t.PID);
				foreach (var r in t.Rows)
				{
					try
					{
						var row = new object[(r.MaxIDX + 1)]; //creating array with 'null values' till MaxIDX+1 (idx is 0 based)
						foreach (Column col in r.Columns)
						{
							row[col.IDX] = col.Value;
						}

						//setrow to avoid issues with AutoIncrement and missing columns (null) if any.
						protocol.AddRow(t.PID, row);
					}
					catch
					{
						error.ErrorOccured = true;
						error.AddMessage(Error.AppendPolicy.AfterLastPart, "Failed to Load Row with Key ", r.Key);
					}
				}
			}
		}
	}

	/// <summary>
	/// Error class can be accessed from all other classes to set the state
	/// </summary>
	public class Error
	{
		public bool ErrorOccured = false;
		private readonly StringBuilder state = new StringBuilder();

		public enum AppendPolicy
		{
			/// <summary>
			///  Add no newlines.
			/// </summary>
			None,
			/// <summary>
			///  Add a newline at the end of the whole message
			/// </summary>
			AfterLastPart,
			/// <summary>
			///  Add a newline after every part provided
			/// </summary>
			AfterEveryPart
		}

		public void SetMessage(object message)
		{
			SetMessage(AppendPolicy.None, message);
		}

		public void SetMessage(AppendPolicy appendPolicy, params object[] messageParts)
		{
			state.Length = 0;
			AddMessage(appendPolicy, messageParts);
		}

		public void AddMessage(object message)
		{
			AddMessage(AppendPolicy.None, message);
		}
		public void AddMessage(AppendPolicy appendPolicy, params object[] messageParts)
		{
			foreach (var messagePart in messageParts)
			{
				state.Append(messagePart);
				if (appendPolicy == AppendPolicy.AfterEveryPart)
				{
					state.AppendLine();
				}
			}

			if (appendPolicy == AppendPolicy.AfterLastPart)
			{
				state.AppendLine();
			}
		}

		public void PrefixMessage(bool appendLine, object message)
		{
			state.Insert(0, Environment.NewLine);
			state.Insert(0, message);
		}

		public string GetMessage()
		{
			return state.ToString();
		}
	}

	public static class ProtocolExtensions
	{
		public enum FileChangeType
		{
			Changed = 32,
			Removed = 33,
			Added = 34
		}

		public static void FileChanged(this SLProtocol protocol, string fullPath, FileChangeType type)
		{
			protocol.NotifyDataMiner(41 /*NT_SEND_DMS_FILE_CHANGE*/, fullPath, (int)type);
		}

		public static void CreateFolder(this SLProtocol protocol, string path)
		{
			protocol.NotifyDataMiner(181 /*NT_CREATE_FOLDER*/, path, null);
		}
	}

