using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Text;
using Skyline.DataMiner.Scripting;
using System.Linq;
public class QAction
{
	/// <summary>
	/// LoadConfigurations
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer</param>
	public static void Run(SLProtocol protocol)
	{
        const string saveLocation = @"C:\Skyline DataMiner\Documents\SLA Configurations\";
        if (Directory.Exists(saveLocation))
        {
            string[] allFiles = Directory.GetFiles(saveLocation);
             string allFilesDiscreet = "";
            if (allFiles != null && allFiles.Length > 0)
            {
                allFilesDiscreet = string.Join(";", allFiles.Select(p=>Path.GetFileNameWithoutExtension(p)));
            }
            protocol.SetParameter(1062, allFilesDiscreet);
        }
	}
}


using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Text;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Stop Outage
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer</param>
	public static void Run(SLProtocolExt protocol, Object input)
	{
		if (protocol.IsEmpty(2001))
			return;

		var strKey = Convert.ToString(protocol.GetParameter(2001));
		if (protocol.array_alarm_store.Exists(strKey))
		{
			switch (Convert.ToUInt32(input))
			{
				case 1:
					//Delete
					protocol.DeleteRow(37, strKey);
					break;
				case 2:
					//Stop
					var currentRow = (Object[])protocol.array_alarm_store.GetRow(strKey);
					var setRow = new Object[currentRow.Length]; 

					var strEnd = DateTime.Now.ToString("yyyy/MM/dd HH:mm:ss");
					var dtEnd = DateTime.Parse(strEnd);

					setRow[Parameter.Array_alarm_store.Idx.column_end_timestamp] = strEnd;

					DateTime dtStart;
					if (ConvertToDateTime(Convert.ToString(currentRow[Parameter.Array_alarm_store.Idx.column_timestamp]), out dtStart))
					{
						var dblOutageWeight = Convert.ToDouble(currentRow[Parameter.Array_alarm_store.Idx.column_impact]);
						var dblOutage = (dtEnd - dtStart).TotalSeconds;
						setRow[Parameter.Array_alarm_store.Idx.column_outage] = dblOutage / 60;

						var dbldelay = (Double)protocol.GetParameter(108);
						var dblThreshold = (Double)protocol.GetParameter(111);
						var dblOutageCorrected = dblOutage;
						var dblViolation = 0.0;

						//Only let outage times bigger than delaytime through
						if (dbldelay > 0.0 && dblOutage < dbldelay)
							dblOutageCorrected = 0.0;

						//subtract minimum outage threshold of the outage time
						if (dblThreshold > 0 && dblOutage > dblThreshold)
							dblOutageCorrected = dblOutage - dblThreshold;
						else if (dblThreshold != 0)
							dblOutageCorrected = 0;

						//Calculate Violation
						dblViolation = dblOutageCorrected * dblOutageWeight / 100;

						setRow[Parameter.Array_alarm_store.Idx.column_outage_corrected] = dblOutageCorrected / 60;
						setRow[Parameter.Array_alarm_store.Idx.column_violation] = dblViolation / 60;

						protocol.SetRow(protocol.array_alarm_store.TableId, strKey, setRow);
					}
					else
					{
						protocol.Log(String.Format("QA{0} STOP OUTAGE - Unable to convert the StartDateTime {1}", protocol.QActionID, Convert.ToString(currentRow[Parameter.Array_alarm_store.Idx.column_timestamp])), LogType.Error, LogLevel.NoLogging);
					}
					break;
				default:
					break;
			}
		}
	}
	/// <summary>
	/// convert to datetime that is expected to succeed
	/// </summary>
	/// <param name="sDateTime"></param>
	/// <returns></returns>
	public static Boolean ConvertToDateTime(String sDateTime, out DateTime dateTime)
	{
		dateTime = DateTime.Now;
		try
		{
			dateTime = DateTime.Parse(sDateTime);
			return true;
		}
		catch
		{ return false; }
	}
}
